#include "drivers/HTPB-driver.h"

task main() {
  // Local variables
  int inputdata;

  while(true) {
    // Read a 10bit wide analogue value from A0
    inputdata = HTPBreadADC(HTPB, 0, 10);

    eraseDisplay();
    nxtDisplayTextLine(1, "%d", inputdata);

    wait1Msec(50);
  }
}

